Export.CSP:
'use strict';

// login service
function LoginSrvc(RESTSrvc) {    
  return {
    auth: 
      function(baseAuth) {
        return RESTSrvc.getPromise({
                                    method: 'GET', 
                                    url: RESTWebApp.appName + '/tasks', 
                                    headers: {'Authorization' : baseAuth }
                                  });
      }
  }
};

// resolving minification problems
LoginSrvc.$inject = ['RESTSrvc'];
servicesModule.factory('LoginSrvc', LoginSrvc);
