Export.CSP:
'use strict';

// Worklist service
function WorklistSrvc( RESTSrvc ) {    
  return {
    // save worklist object
    save: 
      function( worklist ) {
            return RESTSrvc.getPromise({method: 'POST', url: DemoSetting.appName + '/tasks/' + worklist._id, data: worklist});
      },

    // get worklist by id 
    get: 
      function( id ) {
        return RESTSrvc.getPromise({method: 'GET', url: DemoSetting.appName + '/tasks/' + id});
      },

    // get all worklists for current user
    getAll: 
      function() {
        return RESTSrvc.getPromise({method: 'GET', url: DemoSetting.appName + '/tasks'});
      }
  }
};

// resolving minification problems
WorklistSrvc.$inject = [ 'RESTSrvc' ];
servicesModule.factory( 'WorklistSrvc', WorklistSrvc );
